INSERT INTO `post` (`id`, `nome`, `post`, `idpost`, `datacadastro`) VALUES
(62246, 'Gutierrez Louzada da Silva', 'Boa noite gente! Estou tentando instalar um Script e da isso daqui como erro quando eu tento acessar: "Atenção: o script não esta configurado no site, verifique o arquivo mainfile.php" \n \nja fiz de tudo nesse arquivo mainfile.php em relação a conectar com o banco de dados, alguem pode me dar uma luz?\n\nif ( !defined("XOOPS_MAINFILE_INCLUDED") ) {\n define("XOOPS_MAINFILE_INCLUDED",1);\n\n // CAMINHO FÍSICO DO SITE\n // Caminho físico do seu site ou diretório principal sem barra\n // Exemplo: define(''XOOPS_ROOT_PATH'', ''/home/bazik/www/scripts/catalogo1'');\n define(''XOOPS_ROOT_PATH'', ''catalogo'');\n\n // CAMINHO VIRTUAL DO SITE (URL)\n // Caminho virtual do seu site ou diretório principal sem barra\n // Exemplo: define(''XOOPS_URL'', ''http://www.bazik.com.br/scripts/catalogo1'');\n define(''XOOPS_URL'', ''http://www.louzadaweb.qlix.com.br/catalogo'');\n\n define(''XOOPS_CHECK_PATH'', 1);\n // Proteje contra a execução de scripts externos se o modo seguro nao estiver ativo\n if ( XOOPS_CHECK_PATH && !@ini_get(''safe_mode'') ) {\n if ( function_exists(''debug_backtrace'') ) {\n $xoopsScriptPath = debug_backtrace();\n if ( !count($xoopsScriptPath) ) {\n   die("Atenção: esse arquivo não pode ser acessado.");\n }\n $xoopsScriptPath = $xoopsScriptPath[0][''file''];\n } else {\n $xoopsScriptPath = isset($_SERVER[''PATH_TRANSLATED'']) ? $_SERVER[''PATH_TRANSLATED''] :  $_SERVER[''SCRIPT_FILENAME''];\n }\n if ( DIRECTORY_SEPARATOR != ''/'' ) {\n // IIS6 pode dobrar \\ caracteres\n $xoopsScriptPath = str_replace( strpos( $xoopsScriptPath, ''\\\\\\\\'', 2 ) ? ''\\\\\\\\'' : DIRECTORY_SEPARATOR, ''/'', $xoopsScriptPath);\n }\n if ( strcasecmp( substr($xoopsScriptPath, 0, strlen(XOOPS_ROOT_PATH)), str_replace( DIRECTORY_SEPARATOR, ''/'', XOOPS_ROOT_PATH)) ) {\n   exit("Atenção: o script não esta configurado no site, verifique o arquivo mainfile.php.");\n }\n }\n\n // BANCO DE DADOS\n // Escolha o tipo de banco de dados a ser utilizado\n define(''XOOPS_DB_TYPE'', ''mysql'');\n\n // PREFIXO DE TABELA\n // Este prefixo será adicionado a todas as novas tabelas criadas para evitar conflito de nomes no banco de dados. Se você não souber, é só usar o padrão ''xoops''.\n define(''XOOPS_DB_PREFIX'', ''xoops'');\n\n // HOSTNAME DO BANCO DE DADOS\n // Hostname do servidor de banco de dados. Se não sabe, geralmente é ''localhost'' em muitos casos.\n define(''XOOPS_DB_HOST'', ''mysql.qlix.com.br'');\n\n // USUÁRIO DO BANCO DE DADOS\n // Sua conta de usuário do banco de dados no host\n define(''XOOPS_DB_USER'', ''louzadaweb'');\n\n // SENHA DO BANCO DE DADOS\n // Senha para sua conta de usuário do banco de dados\n define(''XOOPS_DB_PASS'', ''silver'');\n\n // NOME DO BANCO DE DADOS\n // O nome do banco de dados no host. O instalador tentará criar o banco de dados se não existir\n define(''XOOPS_DB_NAME'', ''louzadaweb'');\n\n // USAR CONEXÃO PERSISTENTE? (Sim=1 NÃo=0)\n // O padrão é ''Sim''. Escolha ''Sim'' se você não tiver certeza.\n define(''XOOPS_DB_PCONNECT'', 0);\n\n define(''XOOPS_GROUP_ADMIN'', ''1'');\n define(''XOOPS_GROUP_USERS'', ''2'');\n define(''XOOPS_GROUP_ANONYMOUS'', ''3'');\n\n    foreach ( array(''GLOBALS'', ''_SESSION'', ''HTTP_SESSION_VARS'', ''_GET'', ''HTTP_GET_VARS'', ''_POST'', ''HTTP_POST_VARS'', ''_COOKIE'', ''HTTP_COOKIE_VARS'', ''_REQUEST'', ''_SERVER'', ''HTTP_SERVER_VARS'', ''_ENV'', ''HTTP_ENV_VARS'', ''_FILES'', ''HTTP_POST_FILES'', ''xoopsDB'', ''xoopsUser'', ''xoopsUserId'', ''xoopsUserGroups'', ''xoopsUserIsAdmin'', ''xoopsConfig'', ''xoopsOption'', ''xoopsModule'', ''xoopsModuleConfig'', ''xoopsRequestUri'') as $bad_global ) {\n        if ( isset( $_REQUEST[$bad_global] ) ) {\n            header( ''Location: ''.XOOPS_URL.''/'' );\n            exit();\n        }\n    }\n\n if (!isset($xoopsOption[''nocommon'']) && XOOPS_ROOT_PATH != '''') {\n include XOOPS_ROOT_PATH."/include/common.php";\n }\n}\n?>', '14811750159_10152275274725160', '2012-11-05 23:35:43'),
(62247, 'Pereira Barros', 'Galera estou desesperado para resolver um problema que me deparei nesse final de semana.\n\nhttp://stackoverflow.com/questions/13232175/bytearray-readfloat-issue-actionscript-3\n\nSei que não e sobre PHP, por isso queria apelar pelo programador que existe dentro de vocês alem da linguagem.\n\nSe alguem puder me dar uma mao ia ficar eternamente grato.', '14811750159_10152274082960160', '2012-11-05 12:45:58'),
